import Layout from '../../components/layout'
import { getAllPostIds, getPostData } from '../../lib/posts'
import Head from 'next/head'

export default function Post({ postData }) {
  return (
  <Layout>
    {postData.title}
    <br />
    {postData.id}
    <br />
    {postData.date}
    <div dangerouslySetInnerHTML={{ __html:postData.contentHtml }} />
  </Layout>)
}

export async function getStaticPaths() {  // these paths are an array of objects
  const paths = getAllPostIds()
  return {
    paths,
    fallback: false
  }
}
export async function getStaticProps({ params }) {  // params comes from context.params; hence the destructuring
  const postData = await getPostData(params.id)  // id is from the url
  return {
    props: {
      postData
    }
  }
}
